mapper.sql:
SELECT
COUNT(1)
FROM
t_cheliang c
WHERE 1 = 1

    AND c.f_id = #{cheliang.fid}


    AND c.f_type = #{cheliang.FType}


    AND c.f_struct_number = #{cheliang.fstructNumber}


    AND c.f_color = #{cheliang.fcolor}


    AND c.f_volume = #{cheliang.fvolume}


    AND c.f_weight = #{cheliang.fweight}


    AND c.f_date = #{cheliang.fdate}


    AND c.f_turbo = #{cheliang.fturbo}


    AND c.f_turbo = #{cheliang.fdrive}


    AND c.f_min_price = #{cheliang.fminPrice}


    AND c.f_part = #{cheliang.fpart}


    AND c.f_insure = #{cheliang.finsure}


    AND c.f_other = #{cheliang.fother}


    AND c.f_care_counts = #{cheliang.fcareCounts}

SELECT c.f_id fid, c.f_type ftype, c.f_struct_number fstructNumber, c.f_color fcolor, c.f_volume fvolume, c.f_weight fweight, c.f_date fdate, c.f_turbo fturbo, c.f_drive fdrive, c.f_min_price fminPrice, c.f_part fpart, c.f_insure finsure, c.f_other fother, c.f_care_counts fcareCounts FROM t_cheliang c WHERE 1 = 1
AND c.f_id = #{cheliang.fid}
AND c.f_type = #{cheliang.ftype}
AND c.f_struct_number = #{cheliang.fstructNumber}
AND c.f_color = #{cheliang.fcolor}
AND c.f_volume = #{cheliang.fvolume}
AND c.f_weight = #{cheliang.fweight}
AND c.f_date = #{cheliang.fdate}
AND c.f_turbo = #{cheliang.fturbo}
AND c.f_turbo = #{cheliang.fdrive}
AND c.f_min_price = #{cheliang.fminPrice}
AND c.f_part = #{cheliang.fpart}
AND c.f_insure = #{cheliang.finsure}
AND c.f_other = #{cheliang.fother}
AND c.f_care_counts = #{cheliang.fcareCounts}
GROUP BY c.f_id, c.f_type, c.f_struct_number, c.f_color, c.f_volume, c.f_weight, c.f_date, c.f_turbo, c.f_drive, c.f_min_price, c.f_part, c.f_insure, c.f_other, c.f_care_counts
